import json
import os
from urllib.parse import urlparse

from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, Output
from langflow.schema.message import Message


class DomainUpdater(Component):
    display_name = "Domain Updater"
    description = "Updates the persistent domain reputation hashmap using JSON input."
    icon = "database"
    name = "DomainUpdater"

    STORAGE_FILE = "domain_reputation.json"

    inputs = [
        MessageTextInput(
            name="json_input",
            display_name="JSON Input",
            required=True,
            info="Provide JSON: { raw_url: <url>, value: <int> }",
        ),
    ]

    outputs = [
        Output(
            display_name="Updated Output",
            name="result",
            method="update_map",
            output_type="Message"
        ),
    ]

    # ----------------- Helpers -----------------

    def load_map(self):
        if not os.path.exists(self.STORAGE_FILE):
            with open(self.STORAGE_FILE, "w") as f:
                json.dump({}, f)
        with open(self.STORAGE_FILE, "r") as f:
            return json.load(f)

    def save_map(self, store):
        with open(self.STORAGE_FILE, "w") as f:
            json.dump(store, f, indent=2)

    def extract_domain(self, url):
        parsed = urlparse(url)
        return parsed.netloc.lower().replace("www.", "")

    # ----------------- Main Logic -----------------

    def update_map(self) -> Message:
        try:
            # Parse input JSON
            data = json.loads(self.json_input)
            raw_url = data.get("raw_url", "").strip()
            value = int(data.get("value", 1))

            # Fallback for missing URL
            if not raw_url:
                raw_url = "https://google.com"

            domain = self.extract_domain(raw_url)

            # Update stored JSON file
            store = self.load_map()
            store[domain] = value
            self.save_map(store)

            # Return same JSON structure
            output_data = {
                "raw_url": raw_url,
                "safe": value
            }

            text_block = f"""{json.dumps(output_data, indent=2, ensure_ascii=False)}"""

            return Message(text=text_block, data=output_data)

        except Exception as e:
            error_data = {"error": str(e)}
            return Message(
                text=json.dumps(error_data, indent=2),
                data=error_data
            )

